import React from 'react';
import './Banner.css';

const Banner = () => {
  return (
    <header className='banner' style={{backgroundImage:`url('https://thelesfilms.files.wordpress.com/2016/06/netflix-banner.jpg')`, backgroundSize:"cover",
    backgroundPosition: "center center",
    }}>

      <div className="banner_contents">
        <h1 className='banner_itle'>Movie Name</h1>
      </div>
      <div className="banner_buttons">
        <button>Play</button>
        <button>My List</button>
      </div>



    </header>
  )
}

export default Banner;
