import React, { useState } from 'react';
import './Banner.css';
import axios from './axios'
import requests from './request';

const [movie,setMovie ] = useState([]);




useEffect(() => {
 const fetchData = async () =>{
  const request = await axios.get(requests.fetchNetflixoriginals);
  setMovie(
    request.data.results[Math.floor(math.random()* request.data.requests.length -1)]
  )
 }

}, [])


const truncate = (string,n) =>{
  return string.length > n ? string.substr(0, n-1) + '......': string;

}




const Banner = () => {
  return (
    <header className='banner' style={{backgroundImage:`url('https://upload.wikimedia.org/wikipedia/commons/thumb/c/cd/Black_flag.svg/1200px-Black_flag.svg.png')`, backgroundSize:"cover",
    backgroundPosition: "center center",
    }}>

      <div className="banner_contents">
        <h1 className='banner_title'>Movie Name</h1>

      <div className="banner_buttons">
        <button className='banner_button'>Play</button>
        <button className='banner_button'>My List</button>
      </div>

      <h1 className='banner_description'>
{  truncate(`This is a test description This is a test description`, 150)      
}      </h1>
      </div>

      < div className="banner--fadeBottom"/>






    </header>
  )
}

export default Banner;
